--OL cp_msg_template
insert into cp_msg_template (TEMPLATE_CODE, TEMPLATE_NAME, MSG_SUBJECT, MSG_CONTENT, PRODUCT_CODE, SEND_LEVEL, DISP_LEVEL, TIME_FLAG, START_TIME1, END_TIME1, START_TIME2, END_TIME2, WEEKEND_FLAG, HOLIDAY_FLAG, DESCRIPTION, TEMPLATE_STATUS, MSG_TYPE, MSG_CHANNEL, TRANS_CODE) values ('SOCIAL_001', '好友分享动态', '好友动态-分享', '<p> 您的好友${friendsName}分享了：${friendsMsgContent}。</p><p> <a style="text-decoration: underline; background-color: rgb(255, 255, 255); color: rgb(0, 176,80);" href="${friendsActivityURL}" target="_blank"><span style="background-color: rgb(255, 255,255); color: rgb(0, 176, 80);"><strong>去看看</strong></span></a></p>', 'OL', 2, 2, '0', '', '', '', '', '1', '', '用户A因分享产生的一条消息，他的好友都会收到来自于A的这条动态消息。【好友动态】', '1', '01', '1', 'SOCIAL_001');
insert into cp_msg_template (TEMPLATE_CODE, TEMPLATE_NAME, MSG_SUBJECT, MSG_CONTENT, PRODUCT_CODE, SEND_LEVEL, DISP_LEVEL, TIME_FLAG, START_TIME1, END_TIME1, START_TIME2, END_TIME2, WEEKEND_FLAG, HOLIDAY_FLAG, DESCRIPTION, TEMPLATE_STATUS, MSG_TYPE, MSG_CHANNEL, TRANS_CODE) values ('SOCIAL_002', '好友评价动态', '好友动态-评价', '<p>您的好友${friendsName}发表了评价：${friendsMsgContent}。</p><p><a style="text-decoration: underline; background-color: rgb(255, 255, 255); color: rgb(0, 176, 80);" href="${friendsActivityURL}" target="_target"><span style="background-color: rgb(255, 255, 255); color: rgb(0, 176, 80);">去看看</span></a></p>', 'OL', 2, 2, '0', '', '', '', '', '1', '', '用户A评价了一条消息，他的好友都能够看到他的评价！', '1', '01', '1', 'SOCIAL_002');

commit;

